fig = plt.figure()
ax = fig.gca(projection='3d')

def get_surface(index):
  x = []
  y = []
  z = []
  for key1, value1 in total.items():
    for key2, value2 in value1.items():
      x.append(key1)
      y.append(key2*10e8)
      z.append(total[key1][key2][index])
  
  xi = linspace(min(x), max(x), 10)
  yi = linspace(min(y), max(y), 10)
  Z = griddata(x, y, z, xi, yi, interp='linear')
  X, Y = meshgrid(xi, yi)
  return X, Y, Z
  
def plot_comparison(index1, index2, ax):
  X1, Y1, Z1 = get_surface(index1)
  X2, Y2, Z2 = get_surface(index2)
  surf = ax.plot_surface(X1, Y1, Z1, cmap=cm.Greys, linewidth=0, antialiased=False)
  wire = ax.plot_wireframe(X2, Y2, Z2, color='b')

vs = 5
labels = ['DC', 'LEACH', 'MTE', 'FCMMPSO', 'FCM+PSO', 'ECCA']
plot_comparison(3, vs, ax)
