ax.set_ylabel('Electronics energy (nJ/bit)')
ax.set_xlabel('Field length/width (m)')
ax.set_zlabel('Energy dissipation (J)')
plt.show()


def get_average(energy, index):
  x = []
  y = []
  for key1 in [40, 80, 120, 160, 200, 240, 280, 320, 360, 400]:
    x.append(key1)
    y.append(average[key1][energy][index])
  return x, y

def plot_all_averages(energy):
  for i in range(0, 6):
    x, y = get_average(energy, i)
    plt.plot(x, y, label=labels[i])
    plt.legend()
    plt.xlabel('Field length/width (m)')
    plt.ylabel('Average energy spent in one round (J)')

plot_all_averages(40e-9)
plt.show()


